// src/App.js
// src/App.js
import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [file, setFile] = useState(null);
  const [data, setData] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    fetchData();
  }, [search]);

  const fetchData = async () => {
    const response = await fetch(`http://localhost:5000/data?search=${search}`);
    const result = await response.json();
    setData(result);
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append("file", file);

    await fetch("http://localhost:5000/upload", {
      method: "POST",
      body: formData,
    });
    fetchData(); // Refresh the data after upload
  };

  return (
    <div className="App">
      <h1>Excel Data Upload and Dashboard</h1>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleUpload}>Upload</button>
      <input
        type="text"
        placeholder="Search by Client Name"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />
      <table>
        <thead>
          <tr>
            <th>SNo</th>
            <th>Material</th>
            <th>Unit</th>
            <th>Quantity</th>
            <th>Rate</th>
            <th>Amount</th>
            <th>Client Name</th> {/* Add Client Name to table */}
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => (
            <tr key={index}>
              <td>{item.SNo}</td>
              <td>{item.Material}</td>
              <td>{item.Unit}</td>
              <td>{item.Quantity}</td>
              <td>{item.Rate}</td>
              <td>{item.Amount}</td>
              <td>{item.clientname}</td> {/* Display Client Name */}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default App;
